fulfill.run:
if (!$self.value) return;
const projectId = $self.value;
$form.$http.get('/api/v1/forms/data/project/' + projectId)
  .then(res => {
    const data = res.data || {};
    // 基本信息
    $form.setFieldState('projectNumber', state => { state.value = data.projectNumber || ''; });
    $form.setFieldState('projectManagerId', state => { state.value = data.projectManagerId ? String(data.projectManagerId) : ''; });
    $form.setFieldState('responsibleDeptId', state => { state.value = data.responsibleDeptId ? String(data.responsibleDeptId) : ''; });
    $form.setFieldState('projectLevelKey', state => { state.value = data.projectLevelKey || ''; });
    $form.setFieldState('controlLevelKey', state => { state.value = data.controlLevelKey || ''; });
    // 变更前值 - 项目名称
    $form.setFieldState('oldProjectName', state => { state.value = data.projectName || ''; });
    // 变更前值 - 项目编号
    $form.setFieldState('oldProjectNumber', state => { state.value = data.projectNumber || ''; });
    // 变更前值 - 项目成员
    $form.setFieldState('oldProjectManagerId', state => { state.value = data.projectManagerId ? String(data.projectManagerId) : ''; });
    $form.setFieldState('oldProjectMembers', state => { state.value = data.projectMembers || []; });
    // 变更前值 - 审核人员
    $form.setFieldState('oldProjectAuditorId', state => { state.value = data.projectAuditorId ? String(data.projectAuditorId) : ''; });
    $form.setFieldState('oldProjectSupervisorId', state => { state.value = data.projectSupervisorId ? String(data.projectSupervisorId) : ''; });
    // 变更前值 - 计划工期
    $form.setFieldState('oldExpectedStartDate', state => { state.value = data.expectedStartDate || ''; });
    $form.setFieldState('oldExpectedEndDate', state => { state.value = data.expectedEndDate || ''; });
    // 变更前值 - 实际工期
    $form.setFieldState('oldActualStartDate', state => { state.value = data.actualStartDate || ''; });
    $form.setFieldState('oldActualEndDate', state => { state.value = data.actualEndDate || ''; });
    // 变更前值 - 流程管控/项目分级
    $form.setFieldState('oldControlLevelKey', state => { state.value = data.controlLevelKey || ''; });
    $form.setFieldState('oldProjectLevelKey', state => { state.value = data.projectLevelKey || ''; });
  })
  .catch(err => { console.error('加载项目详情失败:', err); });
