fulfill.run:
if (!$self.value) return;
const projectId = $self.value;
$form.$http.get('/api/v1/forms/data/project/' + projectId)
  .then(res => {
    const data = res.data || {};
    $form.setFieldState('projectNumber', state => { state.value = data.projectNumber || ''; });
    $form.setFieldState('projectManagerId', state => { state.value = data.projectManagerId ? String(data.projectManagerId) : ''; });
    $form.setFieldState('projectSupervisorId', state => { state.value = data.projectSupervisorId ? String(data.projectSupervisorId) : ''; });
    $form.setFieldState('projectAuditorId', state => { state.value = data.projectAuditorId ? String(data.projectAuditorId) : ''; });
    $form.setFieldState('responsibleDeptId', state => { state.value = data.responsibleDeptId ? String(data.responsibleDeptId) : ''; });
    $form.setFieldState('projectLevelKey', state => { state.value = data.projectLevelKey || ''; });
    $form.setFieldState('controlLevelKey', state => { state.value = data.controlLevelKey || ''; });
    $form.setFieldState('importanceKey', state => { state.value = data.importanceKey || ''; });

    // 加载角色信息 (架构师方案：中心化角色引用)
    $form.$http.get('/api/v1/projects/' + projectId + '/team-members')
      .then(memberRes => {
        const members = memberRes.data || {};
        const recorder = members.RECORDER || {};

        $form.setFieldState('recorderId', state => {
          state.value = recorder.id ? String(recorder.id) : '';
          if (recorder.id && !recorder.active) {
            state.description = '<span style="color:red;font-weight:bold">原记录人已离职/停用，请重新指定！</span>';
          } else {
            state.description = '';
          }
        });
      });
  })
  .catch(err => { console.error('加载项目详情失败:', err); });
